/* Theme switch */(function(){let lastTap=0;function lightMode(){const currentMode=localStorage.getItem("mode"),newMode=currentMode==="dark"?"light":"dark";localStorage.setItem("mode",newMode);newMode==="light"?document.querySelector("#mainCont").classList.add("light"):document.querySelector("#mainCont").classList.remove("light")}document.body.addEventListener("touchend",function(event){const now=new Date().getTime();if(now-lastTap<=300){lightMode()}lastTap=now});const initialMode=localStorage.getItem("mode");initialMode==="dark"?document.querySelector("#mainCont").classList.remove("light"):document.querySelector("#mainCont").classList.add("light")})();
